import os
import array

rSum =0

def encrypt(pt, key):
	rSum=0
	pVal=0
	ret=[]
	for plainByte,keyByte in zip(pt,key):
		cByte= plainByte^keyByte^pVal^rSum
		pVal=cByte
		rSum=(rSum+pVal)%256
		ret.append(cByte)
	return ret
def decrypt(pt, key):
	rSum=0
	pVal=0
	cByte=0
	ret=[]
	for cByte,keyByte in zip(pt,key):
		plainByte = cByte ^keyByte^pVal^rSum
		pVal = cByte
		rSum = (rSum+pVal)%256
		ret.append(plainByte)
	return ret


